import { createSlice } from "@reduxjs/toolkit";

export const counterSlice = createSlice({
  name: "stock",
  initialState: {
    myStock: [],
    MyBalance: 1000,
  },
  reducers: {
    BuyStock: (state, action) => {
      let stockExists = false;
      state.myStock.forEach((item) => {
        if (item.id === action.payload.id) {
          item.ct += 1;
          stockExists = true;
        }
      });

      if (!stockExists) {
        state.myStock.push({ ...action.payload, ct: 1 });
      }
      state.MyBalance -= action.payload.price;
    },
    SellStock: (state, action) => {
      state.myStock.filter((item) => {
        item.id != action.payload.id;
      });
      state.MyBalance += action.payload.price;
    },
  },
});

export const { BuyStock,SellStock } = counterSlice.actions;

export default counterSlice.reducer;
