// src/store/dataSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunk to fetch data
export const fetchData = createAsyncThunk('data/fetchData', async () => {
  const response = await axios.get('./data.json');
  return response.data;
});

const dataSlice = createSlice({
  name: 'data',
  initialState: {
    items: [],   // Set default to an empty array
    loading: false, // Set default loading state
    error: null,   // Set default error state
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchData.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchData.fulfilled, (state, action) => {
        state.loading = false;
        state.items = action.payload; // Populate items with the data
      })
      .addCase(fetchData.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message; // Capture the error
      });
  },
});

export default dataSlice.reducer;
