import { createSlice } from "@reduxjs/toolkit";

export const MyStockSlice = createSlice({
  name: "stock",
  initialState: {
    myStock: [],
    MyBalance: 1000,
  },
  reducers: {
    BuyStock: (state, action) => {
      let stockExists = false;
      state.myStock.forEach((item) => {
        if (item.id === action.payload.id) {
          item.ct += 1; // Increment count if stock exists
          stockExists = true;
        }
      });

      if (!stockExists) {
        state.myStock.push({ ...action.payload, ct: 1 });
      }
      state.MyBalance -= action.payload.price; // Deduct the price from balance
    },
    SellStock: (state, action) => {
      const updatedStocks = state.myStock.filter((item) => {
        if (item.id === action.payload.id) {
          if (item.ct > 1) {
            item.ct -= 1; // Decrease count if more than one
            return true; // Keep the item in the array
          }
          return false; // Remove item if count reaches zero
        }
        return true; // Keep other items
      });
      state.myStock = updatedStocks; // Update the stock list
      state.MyBalance += action.payload.price; // Add the price back to balance
    },
  },
});

export const { BuyStock, SellStock } = counterSlice.actions;
